mapper.insert:
INSERT INTO pj_post (

)
VALUES (
    #{postId}
,   #{title}
,   #{writer}
,   #{content}
,   0
,   NOW()
,   NULL
,   #{id}
)

mapper.update:
UPDATE pj_post
SET
    modified_date = NOW()
,   title = #{title}
,   content = #{content}
,   writer = #{writer}
WHERE
    post_id = #{postId}
